import React, { useState } from "react";
import { useRef } from "react";
import { useCallback } from "react";
import cn from "classnames";
function Todolist() {
    const [todos, setTodos] = useState([
        {
          id: 1,
          text: "리액트의 기초 알아보기",
          checked: true,
        },
        {
          id: 2,
          text: "컴포넌트 스타일링해 보기",
          checked: true,
        },
        {
          id: 3,
          text: "일정 관리 앱 만들어 보기",
          checked: false,
        },
      ]);
      const nextId = useRef(4);
      const onInsert = useCallback(
        (text) => {
          const todo = {
            id: nextId.current,
            text,
            checked: false,
          };
          setTodos(todos.concat(todo));
          nextId.current += 1;
        },
        [todos]
      );
      const onRemove = useCallback(
        (id) => {
          setTodos(todos.filter((todo) => todo.id !== id));
        },
        [todos]
      );
      const onToggle = useCallback(
        (id) => {
          setTodos(todos.map((todo) => (todo.id === id ? { ...todo, checked: !todo.checked } : todo)));
        },
        [todos]
      );


      const [value, setValue] = useState("");

      const onChange = useCallback((e) => {
        setValue(e.target.value);
      }, []);

      const onSubmit = useCallback(
        (e) => {
          onInsert(value);
          setValue("");

          e.preventDefault();
        },
        [onInsert, value]
      );
    return (
        <div >

            {/* TodoList */}
            <div className="page-todolist">
                <div class="page-title">
                    Todo 리스트
                    <a href="javascript:" className="btn-calen">달력아이콘</a>
                </div>
                <div className="page-contents">
                    <div className="data-day">2023.11.09(목)</div>

                    {/* Form */}

                    <form className="form-type" onSubmit={onSubmit}>
                        <input type="text" placeholder="Todo 리스트를 추가해주세요 " className="form-input" value={value} onChange={onChange}/>
                        <button class="form-button">추가</button>
                    </form> 

                    {/* Form */}
                    <div className="form-type">
                        <input type="text" placeholder="Todo 리스트를 검색해주세요. " className="form-input"/>
                        <a haref="javascript:" class="form-button">검색</a>
                    </div>
                    {/* List */}
                    <div className="wrap-list">
                        {/* Data 가 없을 경우 */}
                        <div className="data-nodata">
                            Todo 리스트가 없습니다.<br/>
                            Todo 리스트를 추가해주세요.

                        </div>
                        <ul className="form-list">
                            {toDos.map((item)=>
                             <li>
                                <input type="checkbox" className={cn("checkbox", { checked })} onClick={() => onToggle(id)}></input>
                                <div className="data-list">
                                {text}
                                </div>
                                <a haref="javascript:" class="form-button type-s-dark" onClick={() => onRemove(id)}>삭제</a>
                            </li>
                            )}
                        </ul> 

                    </div>

                </div>
            </div>

      </div>
    )
}

export default Todolist;
