import React, { useState, useEffect } from "react";
import axios from "axios";

const Todolist = () => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [searchTodo, setSearchTodo] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:3000/todos', { headers: { Authorization: token } })
        .then(res => setTodos(res.data));
    }
  }, []);

//   const addTodo = () => {
//     const token = localStorage.getItem('token');
//     axios.post('http://localhost:3001/todos', { title: newTodo, completed: false }, { headers: { Authorization: token } })
//       .then(response => {
//         console.log('서버 응답 : ', response.data);
//         setTodos(prevTodos => [...prevTodos, response.data]);
//       })
//       .catch(error => console.error('todo 추가 에러 :', error));

//     setNewTodo('');
//   };

    const addTodo = async () => {
    try {
        const token = localStorage.getItem('token');
        const response = await axios.post('http://localhost:3000/todos', { title: newTodo, completed: false }, { headers: { Authorization: token } });

        console.log('서버 응답:', response.data);

    if (response.data && response.data.id && response.data.title) {
        setTodos(prevTodos => [prevTodos, response.data]);
        } else {
        console.error('Invalid response format:', response.data);
        }

        setNewTodo('');
        } catch (error) {
            console.error('Error adding todo:', error);
        }
    };

  const deleteTodo = (id) => {
    const token = localStorage.getItem('token');
    axios.delete(`http://localhost:3000/todos/${id}`, { headers: { Authorization: token } })
      .then(() => setTodos(todos.filter(todo => todo.id !== id)))
      .catch(error => console.error('Error deleting todo:', error));
  };

  const filteredTodos = searchTodo
    ? todos.filter(todo => todo.title.toLowerCase().includes(searchTodo.toLowerCase()))
    : todos;

  return (
    <div>
      {/* TodoList */}
      <div className="page-todolist">
        <div className="page-title">
          Todo 리스트
            {/* <a href="javascript:" className="btn-calen">달력아이콘</a> */}
            <img src="calendar-icon.png" alt="Calendar Icon" className="btn-calen" />
        </div>
        <div className="page-contents">
          <div className="data-day">2023.11.09(목)</div>

            {/* Form */}
            <form className="form-type">
                <input type="text" id="newTodo" name="newTodo" placeholder="Todo 리스트를 추가해주세요 " className="form-input" value={newTodo} onChange={(e) => setNewTodo(e.target.value)} />
                <button className="form-button" onClick={addTodo}>추가</button>
            </form>

            {/* Form */}
            <div className="form-type">
                <input type="text" id="searchTodo" name="searchTodo" placeholder="Todo 리스트를 검색해주세요. " className="form-input" value={searchTodo} onChange={(e) => setSearchTodo(e.target.value)} />
                <button className="form-button" onClick={() => setSearchTodo(searchTodo)}>검색</button>
            </div>

          {/* List */}
          <div className="wrap-list">
            {/* Data 가 없을 경우 */}
            <div className="data-nodata">
              Todo 리스트가 없습니다.<br />
              Todo 리스트를 추가해주세요.
            </div>
            <ul className="form-list">
              {filteredTodos.map(todo => (
                <li key={todo.id}>
                  {todo.title}
                  <input type="checkbox"></input>
                  <div className="data-list">
                    {todo.todo_content}
                  </div>
                  <button className="form-button type-s-dark" onClick={() => deleteTodo(todo.id)}>삭제</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Todolist;





