import React, { useRef, useState, useEffect } from "react";
import axios from "axios";

const Todolist = () => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [searchTodo, setSearchTodo] = useState("");
  const [error, setError] = useState("");
  const contentRef = useRef();
  const [checkedTodos, setCheckedTodos] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      axios
        .get(
          "/todos",
          { validateStatus: false },
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        )
        .then((res) => setTodos(res.data))
        .catch((error) => setError(error.message));
    }
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) {
      alert("할일을 작성해주세요.");
      console.log("할일을 작성해주세요 : 오류");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      setNewTodo(contentRef.current.value);
      setNewTodo(e.current.value);
      console.log(newTodo);

      const response = await axios.post(
        "http://localhost:8080/todos",
        { todo_content: newTodo, completed: false },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("서버 응답:", response.data);

      if (response.data && response.data.success) {
        setTodos((prevTodos) => [...prevTodos, response.data]);
      } else {
        console.error("Invalid response format:", response.data);
      }
      setNewTodo("");
    } catch (error) {
      console.error("에러:", error);
    }
  };



  const handleCheckboxChange = (members_no) => {
    setCheckedTodos((prevChecked) => {
      if (prevChecked.includes(members_no)) {
        return prevChecked.filter((id) => id !== members_no);
      } else {
        return [...prevChecked, members_no];
      }
    });
  };

  const deleteTodo = (members_no) => {
    const token = localStorage.getItem("token");
    const isChecked = checkedTodos.includes(members_no);
    if (isChecked) {
      axios
        .delete(`http://localhost:3000/todos/${members_no}`, {
          headers: { Authorization: token },
        })
        .then(() =>
          setTodos((prevTodos) => prevTodos.filter((todo) => todo.members_no !== members_no))
          )
        .catch((error) => console.error("투두 삭제 오류:", error));
    } else {
      alert("체크박스 체크 후 삭제해주세요");
      console.log("체크박스 체크해야 삭제 가능 오류");
    }
  };

  const filteredTodos = searchTodo
    ? todos.filter((todo) =>
        todo.title.toLowerCase().includes(searchTodo.toLowerCase())
      )
    : todos;

  return (
    <div>
      {/* TodoList */}
      <div className="page-todolist">
        <div className="page-title">
          Todo 리스트
          {/* <a href="javascript:" className="btn-calen">달력아이콘</a> */}
          <img
            src="calendar-icon.png"
            alt="Calendar Icon"
            className="btn-calen"
          />
        </div>
        <div className="page-contents">
          <div className="data-day">2023.11.09(목)</div>

          {/* Form */}
          <form className="form-type" onSubmit={addTodo}>
            <input
              ref={contentRef}
              type="text"
              id="newTodo"
              name="todo_content"
              placeholder="Todo 리스트를 추가해주세요 "
              className="form-input"
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
            />
            <button className="form-button">추가</button>
          </form>

          {/* Form */}
          <div className="form-type">
            <input
              type="text"
              id="searchTodo"
              name="todo_content"
              placeholder="Todo 리스트를 검색해주세요. "
              className="form-input"
              value={searchTodo}
              onChange={(e) => setSearchTodo(e.target.value)}
            />
            <button
              className="form-button"
              onClick={() => setSearchTodo(searchTodo)}
            >
              검색
            </button>
          </div>

          {/* List */}
          <div className="wrap-list">
            {/* Data 가 없을 경우 */}
            {filteredTodos.length === 0 ? (
              <div className="data-nodata">
                Todo 리스트가 없습니다.
                <br />
                Todo 리스트를 추가해주세요.
              </div>
             ) : (

            <ul className="form-list">
              {filteredTodos.map((todo) => (
                <li key={todo.members_no}>
                  {todo.todo_content}
                  <input type="checkbox" 
                  checked={checkedTodos.includes(todo.members_no)} 
                  onChange={() => handleCheckboxChange(todo.members_no)}/>
                  <div className="data-list">{todo.todo_content}</div>
                  <button
                    className="form-button type-s-dark"
                    onClick={() => deleteTodo(todo.members_no)}
                  >
                    삭제
                  </button>
                </li>
              ))}
            </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Todolist;
